mapper.insert:
INSERT INTO off_timetable( time_num, off_num, time_date, time_start, time_end) SELECT off_timetable_seq.nextval, A.* FROM (
SELECT #{item.off_num} AS off_num, #{item.time_date} AS time_date, #{item.time_start} AS time_start, #{item.time_end} AS time_end FROM DUAL
) A

INSERT INTO offclass (off_num, category_num, off_name, off_limit, off_price, off_content, off_filename, off_uploadfile, user_num
,img_file
) VALUES ( #{off_num}, #{category_num}, #{off_name}, #{off_limit}, #{off_price}, #{off_content}, #{off_filename}, #{off_uploadfile}, #{user_num}
,#{img_file}
)
