mapper.insert:
INSERT INTO off_timetable( time_num, off_num, time_date, time_start, time_end, off_limit) SELECT off_timetable_seq.nextval, A.* FROM (
SELECT #{item.off_num} AS off_num, #{item.time_date} AS time_date, #{item.time_start} AS time_start, #{item.time_end} AS time_end, #{item.off_limit} AS off_limit FROM DUAL
) A

mapper.select:
SELECT * FROM off_timetable WHERE off_num=#{off_num} AND CONCAT(TO_CHAR(time_date,'YY/MM/DD '),time_start) >=TO_CHAR(SYSDATE,'YY/MM/DD HH24:MI')
AND time_date=#{time_date}
ORDER BY CONCAT(TO_CHAR(time_date,'YY/MM/DD '),time_start)

mapper.update:
UPDATE offclass SET category_num=#{category_num}, off_name=#{off_name}, off_price=#{off_price}, off_content=#{off_content}, offzipcode=#{offzipcode}, offaddress1=#{offaddress1}, offaddress2=#{offaddress2}
,off_filename=#{off_filename},off_uploadfile=#{off_uploadfile}
WHERE off_num=#{off_num}

mapper.select:
SELECT * FROM offstar s JOIN ouser_detail d ON s.user_num = d.user_num LEFT OUTER JOIN ( SELECT offstar_num,d.user_num AS writer_num,name AS writer_name,offre_content,offre_date,photo_name as writer_photo FROM offstar_reply r JOIN ouser_detail d ON r.user_num = d.user_num ) r ON s.offstar_num = r.offstar_num WHERE off_num=#{off_num}
AND ROWNUM<=#{rownum}
ORDER BY s.offstar_num DESC

SELECT COUNT(*)
		FROM offstar
		WHERE off_num=#{off_num}

		AND rating =#{rating}


		AND rating =#{rating}


		AND rating =#{rating}


		AND rating =#{rating}


		AND rating =#{rating}
